    console.log(state);
    const handleClick= async ()=>{
        const { data } = await axios.post('http://localhost:5000/api/post/'+state.title,{
           name:name,
           review:review
        });
        console.log(state.title);
        navigate(`/home/${state.title}/review`)
        
    }
    return(
        <div>
        <h1>{state.title}</h1>
        <div>
